import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, useNavigate} from 'react-router-dom';


function App() {
  const [currentBackground, setCurrentBackground] = useState("url('./asset/home.png')");
  const navigate = useNavigate;

  const goToPage = (path) => {
    navigate(path);
  };

  const handleCardClick = (cardBg) => {
    if (cardBg && cardBg !== 'none') {
      setCurrentBackground(cardBg);

      document.body.style.filter = "brightness(0.8)";
      setTimeout(() => {
        document.body.style.filter = "brightness(1)";
      }, 300);
    }

  };

  return (
    <div className="App" style={{ backgroundImage: currentBackground }}>
      <div className="overlay" aria-hidden="true"></div>

      <header>
        <div className="logo">Culture Compass</div>

        <nav>
          <a className="active">Home</a>
          <a>Service</a>
          <a>Blogs</a>
          <a>User</a>
        </nav>

        <div className="auth">
          <button className="btn">Sign up</button>
          <button className="btn">Login</button>
        </div>
      </header>

      <main className="hero">
        <section className="left">
          <div className="eyebrow">Culture Compass</div>
          <h1>Khám phá chất Việt qua những hành trình đậm nét văn hóa</h1>
          <p className="lead">
            Trải nghiệm những truyền thống ẩn giấu, những lễ hội chân thực và những hành trình khó quên khắp Việt Nam.
          </p>
          <a className="cta">Start now!</a>
        </section>

        <aside className="right" aria-hidden="true">
          <div className="card-col">
            <div className="img-card" onClick={() => handleCardClick("url('./asset/test3.jpg')")}>
              <div className="inner" style={{ backgroundImage: "url('./asset/test3.jpg')" }}></div>
            </div>
          </div>

          <div className="card-col">
            <div className="img-card" onClick={() => handleCardClick("url('./asset/test2.jpg')")}>
              <div className="inner" style={{ backgroundImage: "url('./asset/test2.jpg')" }}></div>
            </div>
          </div>

          <div className="card-col">
            <div className="img-card" onClick={() => handleCardClick("url('./asset/test1.png')")}>
              <div className="inner" style={{ backgroundImage: "url('./asset/test1.png')" }}></div>
            </div>
          </div>
        </aside>
      </main>

      <footer>
        <small>© 2025 Culture Compass</small>
      </footer>
    </div>
  );
}

export default App;
